function makeSound(e){switch(e){case"a":var a=new Audio("sounds/boom.wav");a.play();break;case"s":var s=new Audio("sounds/clap.wav");s.play();break;case"d":var n=new Audio("sounds/hihat.wav");n.play();break;case"f":var o=new Audio("sounds/kick.wav");o.play();break;case"g":var t=new Audio("sounds/crash.mp3");t.play();break;case"h":var u=new Audio("sounds/ride.wav");u.play();break;case"j":var d=new Audio("sounds/snare.mp3");d.play();break;case"k":var r=new Audio("sounds/tink.wav");r.play();break;case"l":var i=new Audio("sounds/tom.mp3");i.play();break;default:console.log(e)}}function buttonAnimation(e){document.querySelector("."+e).classList.add("pressed"),setTimeout(()=>{document.querySelector("."+e).classList.remove("pressed")},200)}var light=document.querySelector("html").classList;document.addEventListener("keypress",function(e){var a=e.key;makeSound(a),buttonAnimation(a)}),document.addEventListener("click",function(e){var a=e.target.classList[1];makeSound(a),buttonAnimation(a)});